mapper.update:
UPDATE NAG_DATAPOOL
DP_ID=#{dpId}, DP_NAME=#{dpName}, DP_SCRIPTID=#{dpScriptid}, DP_STARTDATE=#{dpStartdate}, DP_ENDDATE=#{dpEnddate}, DP_START=#{dpStart}, DP_TRANSINT=#{dpTransint}, DP_TRANSKEEP=#{dpTranskeep}, DP_DATAASSIGN=#{dpDataassign}, DP_MAXCALLTIMES=#{dpMaxcalltimes}, DP_JUMPSCRIPT=#{dpJumpscript}, DP_DATACOUNT=#{dpDatacount}, DP_NOTASSIGNCOUNT=#{dpNotassigncount}, DP_ANSWERDICT=#{dpAnswerdict}, DP_NOANSWERDICT=#{dpNoanswerdict}, DP_ISCTICONTROL=#{dpIscticontrol}, DP_RULE1=#{dpRule1}, DP_RULE2=#{dpRule2}, DP_RULE3=#{dpRule3}, DP_RULE4=#{dpRule4}, DP_RULE5=#{dpRule5}, DP_RULE6=#{dpRule6}, DP_RULE7=#{dpRule7}, DP_STARTTIME=#{dpStarttime}, DP_ENDTIME=#{dpEndtime}, DP_CANIMPORT=#{dpCanimport}, DP_ISLIMIT=#{dpIslimit}, DP_POOLTYPE=#{dpPooltype}, DP_GIVEUPPOOL=#{dpGiveuppool}, DP_APPROVAL=#{dpApproval}, DP_INCALLTRANIN=#{dpIncalltranin}, DP_Q_ID=#{dpQId}
WHERE DP_ID=#{dpId}

