mapper.select:
	SELECT
			 DP_ID as "dpId" ,			
			 DP_NAME as "dpName" ,			
			 DP_SCRIPTID as "dpScriptid" ,			
			 DP_STARTDATE as "dpStartdate" ,			
			 DP_ENDDATE as "dpEnddate" ,			
			 DP_START as "dpStart" ,			
			 DP_TRANSINT as "dpTransint" ,			
			 DP_TRANSKEEP as "dpTranskeep" ,			
